import React from 'react';

const Home = () => {
  return (
    <div style={styles.container}>
      <div style={styles.hero}>
        <h1 style={styles.title}>Lezzetli Tarifleri Keşfedin</h1>
        <p style={styles.subtitle}>
          Binlerce tarif arasından size uygun olanı bulun veya kendi tariflerinizi paylaşın
        </p>
        <div style={styles.buttonGroup}>
          <button style={styles.button}>Tarifleri Keşfet</button>
          <button style={styles.buttonOutline}>Tarif Ekle</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '2rem 1rem'
  },
  hero: {
    textAlign: 'center',
    padding: '4rem 0'
  },
  title: {
    fontSize: '2.5rem',
    color: '#2c3e50',
    marginBottom: '1rem'
  },
  subtitle: {
    fontSize: '1.2rem',
    color: '#666',
    marginBottom: '2rem'
  },
  buttonGroup: {
    display: 'flex',
    gap: '1rem',
    justifyContent: 'center'
  },
  button: {
    padding: '0.8rem 2rem',
    fontSize: '1rem',
    backgroundColor: '#2c3e50',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  buttonOutline: {
    padding: '0.8rem 2rem',
    fontSize: '1rem',
    backgroundColor: 'transparent',
    color: '#2c3e50',
    border: '2px solid #2c3e50',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default Home;
