import React, { useState } from 'react';
import RecipeCard from './RecipeCard';

const RecipeList = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tümü');
  const [searchTerm, setSearchTerm] = useState('');

  const categories = ['Tümü', 'Çorbalar', 'Ana Yemekler', 'Tatlılar', 'Salatalar', 'Kahvaltılık'];

  const [recipes] = useState([
    {
      id: 1,
      title: "Mercimek Çorbası",
      description: "Geleneksel Türk mutfağının vazgeçilmezi",
      imageUrl: "https://i.lezzet.com.tr/images-xxlarge-secondary/mercimek-corbasi-nasil-yapilir-5c9e0c74-b51f-474d-a397-b2f68c304c04.jpg",
      cookingTime: "45 dakika",
      difficulty: "Kolay",
      category: "Çorbalar"
    },
    {
      id: 2,
      title: "Karnıyarık",
      description: "Enfes patlıcan yemeği",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2020/08/karniyarik-tarifi-2-son.jpg",
      cookingTime: "60 dakika",
      difficulty: "Orta",
      category: "Ana Yemekler"
    },
    {
      id: 3,
      title: "Kazandibi",
      description: "Geleneksel Türk tatlısı",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2022/05/kazandibi-tarifi.jpg",
      cookingTime: "45 dakika",
      difficulty: "Orta",
      category: "Tatlılar"
    },
    {
      id: 4,
      title: "Çoban Salatası",
      description: "Taze ve lezzetli salata",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2023/06/coban-salata-yemekcom.jpg",
      cookingTime: "15 dakika",
      difficulty: "Kolay",
      category: "Salatalar"
    },
    {
      id: 5,
      title: "Menemen",
      description: "Klasik kahvaltılık lezzet",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2022/05/menemen-tarifi.jpg",
      cookingTime: "20 dakika",
      difficulty: "Kolay",
      category: "Kahvaltılık"
    }
  ]);

  const filteredRecipes = recipes.filter(recipe => {
    const matchesCategory = selectedCategory === 'Tümü' || recipe.category === selectedCategory;
    const matchesSearch = recipe.title.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Tarifler</h1>
      
      <div style={styles.filters}>
        <div style={styles.categories}>
          {categories.map(category => (
            <button
              key={category}
              style={{
                ...styles.categoryButton,
                backgroundColor: selectedCategory === category ? '#2c3e50' : '#fff',
                color: selectedCategory === category ? '#fff' : '#2c3e50'
              }}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>

        <div style={styles.searchBar}>
          <input
            type="text"
            placeholder="Tarif ara..."
            style={styles.searchInput}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <div style={styles.grid}>
        {filteredRecipes.map(recipe => (
          <RecipeCard key={recipe.id} recipe={recipe} />
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '2rem'
  },
  title: {
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: '2rem'
  },
  filters: {
    marginBottom: '2rem'
  },
  categories: {
    display: 'flex',
    justifyContent: 'center',
    gap: '1rem',
    marginBottom: '1rem',
    flexWrap: 'wrap'
  },
  categoryButton: {
    padding: '0.5rem 1rem',
    border: '2px solid #2c3e50',
    borderRadius: '20px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    transition: 'all 0.3s ease'
  },
  searchBar: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '1rem'
  },
  searchInput: {
    width: '100%',
    maxWidth: '500px',
    padding: '0.8rem',
    fontSize: '1rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    outline: 'none'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '2rem',
    padding: '1rem'
  }
};

export default RecipeList;
