// src/pages/Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUtensils, FaSearch, FaClock, FaStar, FaHeart } from 'react-icons/fa';
import RecipeSuggestion from '../components/recipe/RecipeSuggestion';
import { getRecipeImage } from '../utils/ImageUtils'; 


const Home = () => {
  const navigate = useNavigate();

  const featuredRecipes = [
    {
      id: 1,
      title: "Mercimek Çorbası",
      image: getRecipeImage("Mercimek Çorbası", "Çorbalar"),
      rating: 4.8,
      time: "45 dk"
    },
    {
      id: 2,
      title: "Karnıyarık",
      image: getRecipeImage("Karnıyarık", "Ana Yemekler"),
      rating: 4.7,
      time: "60 dk"
    },
    {
      id: 3,
      title: "Baklava",
      image: getRecipeImage("Baklava", "Tatlılar"),
      rating: 4.9,
      time: "120 dk"
    }
  ];

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            Türk Mutfağının <span style={styles.highlight}>Lezzetleri</span>
          </h1>
          <p style={styles.heroSubtitle}>
            Geleneksel tariflerden modern yorumlara, binlerce tarif parmaklarınızın ucunda
          </p>
          <div style={styles.heroButtons}>
            <button 
              style={styles.primaryButton}
              onClick={() => navigate('/recipes')}
            >
              <FaUtensils style={{ marginRight: '8px' }} />
              Tarifleri Keşfet
            </button>
            <button 
              style={styles.secondaryButton}
              onClick={() => navigate('/suggestion')}
            >
              <FaHeart style={{ marginRight: '8px' }} />
              Tarif Öner
            </button>
          </div>
        </div>
      </section>

      {/* Öne Çıkan Tarifler */}
      <section>
        <h2 style={styles.sectionTitle}>Öne Çıkan Tarifler</h2>
        <div style={styles.recipeGrid}>
          {featuredRecipes.map(recipe => (
            <div 
              key={recipe.id} 
              style={styles.recipeCard}
              onClick={() => navigate(`/recipe/${recipe.id}`)}
            >
              <img src={recipe.image} alt={recipe.title} style={styles.recipeImage} />
              <div style={styles.recipeOverlay}>
                <div>
                  <div style={styles.recipeRating}>
                    <FaStar /> {recipe.rating}
                  </div>
                  <div style={styles.recipeTime}>
                    <FaClock /> {recipe.time}
                  </div>
                </div>
              </div>
              <h3 style={styles.recipeTitle}>{recipe.title}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Malzeme Tabanlı Öneri Sistemi */}
      <RecipeSuggestion />

      {/* Özellikler */}
      <section style={styles.features}>
        <div style={styles.feature}>
          <FaSearch style={styles.featureIcon} />
          <h3>Kolay Arama</h3>
          <p>İstediğiniz tarifi saniyeler içinde bulun</p>
        </div>
        <div style={styles.feature}>
          <FaClock style={styles.featureIcon} />
          <h3>Hızlı Tarifler</h3>
          <p>30 dakikadan kısa süren pratik tarifler</p>
        </div>
        <div style={styles.feature}>
          <FaStar style={styles.featureIcon} />
          <h3>Kullanıcı Yorumları</h3>
          <p>Denenmiş ve beğenilmiş tarifler</p>
        </div>
      </section>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  },
  hero: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '4rem 0',
    backgroundColor: '#f8f9fa',
    borderRadius: '20px',
    marginBottom: '4rem',
    backgroundImage: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  },
  heroContent: {
    padding: '0 4rem',
    flex: '1',
  },
  heroTitle: {
    fontSize: '3.5rem',
    color: '#2c3e50',
    marginBottom: '1.5rem',
    lineHeight: '1.2',
  },
  highlight: {
    color: '#e74c3c',
    fontWeight: 'bold',
  },
  heroSubtitle: {
    fontSize: '1.2rem',
    color: '#666',
    marginBottom: '2rem',
  },
  heroButtons: {
    display: 'flex',
    gap: '1rem',
  },
  primaryButton: {
    padding: '1rem 2rem',
    backgroundColor: '#e74c3c',
    color: 'white',
    border: 'none',
    borderRadius: '50px',
    fontSize: '1.1rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
  },
  secondaryButton: {
    padding: '1rem 2rem',
    backgroundColor: 'transparent',
    color: '#2c3e50',
    border: '2px solid #2c3e50',
    borderRadius: '50px',
    fontSize: '1.1rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
  },
  sectionTitle: {
    fontSize: '2rem',
    color: '#2c3e50',
    marginBottom: '2rem',
    textAlign: 'center',
  },
  recipeGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '2rem',
    marginBottom: '4rem',
  },
  recipeCard: {
    position: 'relative',
    borderRadius: '12px',
    overflow: 'hidden',
    cursor: 'pointer',
    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
    transition: 'transform 0.2s',
    backgroundColor: 'white',
  },
  recipeImage: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  recipeOverlay: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    padding: '1rem',
    background: 'linear-gradient(transparent, rgba(0,0,0,0.7))',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  recipeTitle: {
    padding: '1rem',
    margin: 0,
    color: '#2c3e50',
    fontSize: '1.2rem',
  },
  recipeRating: {
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    gap: '0.3rem',
  },
  recipeTime: {
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    gap: '0.3rem',
  },
  features: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '2rem',
    padding: '2rem',
    backgroundColor: '#f8f9fa',
    borderRadius: '20px',
  },
  feature: {
    textAlign: 'center',
    padding: '2rem',
  },
  featureIcon: {
    fontSize: '2.5rem',
    color: '#e74c3c',
    marginBottom: '1rem',
  },
};

export default Home;
