// src/components/recipe/RecipeSuggestion.js
import React, { useState } from 'react';
import { FaPlus, FaSearch } from 'react-icons/fa';

const RecipeSuggestion = () => {
  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const commonIngredients = [
    'domates', 'soğan', 'patates', 'havuç', 'biber',
    'yumurta', 'un', 'süt', 'yoğurt', 'peynir',
    'tavuk', 'kıyma', 'pirinç', 'makarna', 'mercimek'
  ];

  const addIngredient = () => {
    if (newIngredient && !ingredients.includes(newIngredient)) {
      setIngredients([...ingredients, newIngredient]);
      setNewIngredient('');
    }
  };

  const removeIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const findRecipes = () => {
    // Yapay zeka önerisi simülasyonu
    const suggestedRecipes = recipes.filter(recipe => {
      // Basit bir eşleştirme algoritması
      const requiredIngredients = recipe.ingredients.map(ing => 
        ing.toLowerCase().split(' ').pop()
      );
      const matchCount = ingredients.filter(ing => 
        requiredIngredients.includes(ing.toLowerCase())
      ).length;
      return matchCount >= Math.min(3, requiredIngredients.length / 2);
    });

    setSuggestions(suggestedRecipes);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Malzemelerinizle Neler Yapabilirsiniz?</h2>
      
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={newIngredient}
          onChange={(e) => setNewIngredient(e.target.value)}
          placeholder="Malzeme ekleyin..."
          style={styles.input}
          list="ingredients"
        />
        <datalist id="ingredients">
          {commonIngredients.map(ing => (
            <option key={ing} value={ing} />
          ))}
        </datalist>
        <button onClick={addIngredient} style={styles.addButton}>
          <FaPlus />
        </button>
      </div>

      <div style={styles.ingredientTags}>
        {ingredients.map((ing, index) => (
          <span key={index} style={styles.tag}>
            {ing}
            <button 
              onClick={() => removeIngredient(index)}
              style={styles.removeTag}
            >
              ×
            </button>
          </span>
        ))}
      </div>

      <button 
        onClick={findRecipes} 
        style={styles.searchButton}
        disabled={ingredients.length === 0}
      >
        <FaSearch /> Tarifleri Bul
      </button>

      {suggestions.length > 0 && (
        <div style={styles.suggestions}>
          <h3>Önerilen Tarifler:</h3>
          <div style={styles.recipeGrid}>
            {suggestions.map(recipe => (
              <div key={recipe.id} style={styles.recipeCard}>
                <img 
                  src={recipe.imageUrl} 
                  alt={recipe.title}
                  style={styles.recipeImage} 
                />
                <div style={styles.recipeInfo}>
                  <h4>{recipe.title}</h4>
                  <p>{recipe.description}</p>
                  <span style={styles.recipeBadge}>
                    {recipe.cookingTime}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    maxWidth: '1200px',
    margin: '0 auto',
  },
  title: {
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: '2rem',
  },
  inputContainer: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem',
  },
  input: {
    flex: 1,
    padding: '0.8rem',
    fontSize: '1rem',
    border: '2px solid #ddd',
    borderRadius: '8px',
    outline: 'none',
  },
  addButton: {
    padding: '0.8rem',
    backgroundColor: '#2ecc71',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
  },
  ingredientTags: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.5rem',
    marginBottom: '1rem',
  },
  tag: {
    backgroundColor: '#3498db',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '20px',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
  },
  removeTag: {
    background: 'none',
    border: 'none',
    color: 'white',
    cursor: 'pointer',
    fontSize: '1.2rem',
  },
  searchButton: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    padding: '1rem 2rem',
    backgroundColor: '#e74c3c',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    margin: '1rem auto',
    fontSize: '1.1rem',
  },
  suggestions: {
    marginTop: '2rem',
  },
  recipeGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1.5rem',
    marginTop: '1rem',
  },
  recipeCard: {
    backgroundColor: 'white',
    borderRadius: '12px',
    overflow: 'hidden',
    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
    transition: 'transform 0.2s',
    cursor: 'pointer',
    '&:hover': {
      transform: 'translateY(-5px)',
    },
  },
  recipeImage: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  recipeInfo: {
    padding: '1rem',
  },
  recipeBadge: {
    backgroundColor: '#f0f0f0',
    padding: '0.3rem 0.8rem',
    borderRadius: '12px',
    fontSize: '0.9rem',
    color: '#666',
  },
};

export default RecipeSuggestion;
