// src/utils/imageUtils.js

const COLLECTION_IDS = {
    general: '4316154',    // Genel yemek fotoğrafları
    turkish: '8984561',    // Türk yemekleri
    desserts: '3678513',   // Tatlılar
    soups: '2341675'       // Çorbalar
  };
  
  const getRecipeImage = (title, category, width = 800, height = 600) => {
    // Türkçe karakterleri İngilizce karakterlere çevirme
    const searchTerm = title
      .toLowerCase()
      .replace(/ç/g, 'c')
      .replace(/ğ/g, 'g')
      .replace(/ı/g, 'i')
      .replace(/ö/g, 'o')
      .replace(/ş/g, 's')
      .replace(/ü/g, 'u')
      .split(' ')
      .join('-');
  
    // Kategoriye göre koleksiyon seçimi
    let collectionId = COLLECTION_IDS.general;
    if (category === 'Tatlılar') collectionId = COLLECTION_IDS.desserts;
    if (category === 'Çorbalar') collectionId = COLLECTION_IDS.soups;
  
    return `https://source.unsplash.com/collection/${collectionId}/${width}x${height}/?${searchTerm}`;
  };
  
  export default getRecipeImage;
