import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import CommentSection from './CommentSection';

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState({
    title: "Mercimek Çorbası",
    description: "Geleneksel Türk mutfağının vazgeçilmez çorbası",
    imageUrl: "https://source.unsplash.com/featured/?lentil,soup",
    cookingTime: "45 dakika",
    difficulty: "Kolay",
    servings: "4-6 kişilik",
    ingredients: [
      "2 su bardağı kırmızı mercimek",
      "1 adet soğan",
      "2 adet havuç",
      "2 yemek kaşığı un",
      "1 yemek kaşığı tereyağı",
      "Tuz, karabiber",
      "7 su bardağı su"
    ],
    instructions: [
      "Mercimekleri yıkayıp süzün.",
      "Soğanları ve havuçları küçük küçük doğrayın.",
      "Tencerede tereyağını eritip soğanları kavurun.",
      "Havuçları ekleyip 2-3 dakika daha kavurun.",
      "Unu ekleyip kokusu çıkana kadar kavurun.",
      "Mercimek ve suyu ekleyip karıştırın.",
      "Kaynayınca kısık ateşte 30 dakika pişirin.",
      "Blenderdan geçirip tuz ve karabiber ekleyin."
    ]
  });

  useEffect(() => {
    // API'den tarif detayları burada çekilecek
    console.log('Tarif ID:', id);
  }, [id]);

  return (
    <div style={styles.container}>
      <div style={styles.imageContainer}>
        <img src={recipe.imageUrl} alt={recipe.title} style={styles.image} />
      </div>
      
      <div style={styles.content}>
        <h1 style={styles.title}>{recipe.title}</h1>
        
        <div style={styles.badges}>
          <span style={styles.badge}>{recipe.cookingTime}</span>
          <span style={styles.badge}>{recipe.difficulty}</span>
          <span style={styles.badge}>{recipe.servings}</span>
        </div>

        <p style={styles.description}>{recipe.description}</p>

        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Malzemeler</h2>
          <ul style={styles.list}>
            {recipe.ingredients.map((ingredient, index) => (
              <li key={index} style={styles.listItem}>{ingredient}</li>
            ))}
          </ul>
        </div>

        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Hazırlanışı</h2>
          <ol style={styles.list}>
            {recipe.instructions.map((instruction, index) => (
              <li key={index} style={styles.listItem}>{instruction}</li>
            ))}
          </ol>
        </div>

        <CommentSection recipeId={id} />
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1000px',
    margin: '0 auto',
    padding: '2rem'
  },
  imageContainer: {
    width: '100%',
    height: '400px',
    overflow: 'hidden',
    borderRadius: '8px',
    marginBottom: '2rem'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover'
  },
  content: {
    padding: '0 1rem'
  },
  title: {
    fontSize: '2.5rem',
    color: '#2c3e50',
    marginBottom: '1rem'
  },
  badges: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '2rem'
  },
  badge: {
    backgroundColor: '#e9ecef',
    padding: '0.5rem 1rem',
    borderRadius: '20px',
    fontSize: '0.9rem',
    color: '#495057'
  },
  description: {
    fontSize: '1.1rem',
    color: '#666',
    lineHeight: '1.6',
    marginBottom: '2rem'
  },
  section: {
    marginBottom: '2rem'
  },
  sectionTitle: {
    fontSize: '1.5rem',
    color: '#2c3e50',
    marginBottom: '1rem'
  },
  list: {
    paddingLeft: '1.5rem'
  },
  listItem: {
    marginBottom: '0.5rem',
    lineHeight: '1.6',
    color: '#495057'
  }
};

export default RecipeDetail;
