import axios from 'axios';

// API anahtarınızı buraya ekleyin
const API_KEY = 'bZJJR7N-xwnVxUE--r3mQAz7kV5baGMGPcCD_7kdRw8';  // Buraya kendi API anahtarınızı koymalısınız

const fetchRecipeImage = async (query) => {
  try {
    // Unsplash API'sine istek gönderiyoruz
    const response = await axios.get('https://api.unsplash.com/photos/random', {
      params: {
        query: query, // Tarifi temsil eden malzeme adını kullanıyoruz
        client_id: API_KEY, // API anahtarınızı buraya ekliyoruz
        orientation: 'landscape', // Görselin yatay olmasını isteyebilirsiniz
      },
    });

    // Eğer herhangi bir resim bulunursa, ilk resmi döndürüyoruz
    return response.data[0]?.urls?.regular || 'https://via.placeholder.com/200'; // Default görsel döndürüyoruz
  } catch (error) {
    console.error("Error fetching image:", error);
    return 'https://via.placeholder.com/200'; // Hata durumunda fallback görseli döndürüyoruz
  }
};

export default fetchRecipeImage;
