import React, { useState } from 'react';
import RecipeCard from './RecipeCard';

const RecipeList = () => {
  const [recipes] = useState([
    {
      id: 1,
      title: "Mercimek Çorbası",
      description: "Geleneksel Türk mutfağının vazgeçilmezi",
      imageUrl: "https://i.lezzet.com.tr/images-xxlarge-secondary/mercimek-corbasi-nasil-yapilir-5c9e0c74-b51f-474d-a397-b2f68c304c04.jpg",
      cookingTime: "45 dakika",
      difficulty: "Kolay"
    },
    {
      id: 2,
      title: "Karnıyarık",
      description: "Enfes patlıcan yemeği",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2020/08/karniyarik-tarifi-2-son.jpg",
      cookingTime: "60 dakika",
      difficulty: "Orta"
    },
    {
      id: 3,
      title: "Mantı",
      description: "El yapımı geleneksel mantı",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2022/03/manti-tarifi.jpg",
      cookingTime: "90 dakika",
      difficulty: "Zor"
    },
    {
      id: 4,
      title: "İskender Kebap",
      description: "Meşhur Bursa İskenderi",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2023/01/iskender-kebap-yemekcom.jpg",
      cookingTime: "40 dakika",
      difficulty: "Orta"
    },
    {
      id: 5,
      title: "Lahmacun",
      description: "Geleneksel Türk pizzası",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2022/03/lahmacun-tarifi.jpg",
      cookingTime: "30 dakika",
      difficulty: "Orta"
    },
    {
      id: 6,
      title: "Kuru Fasulye",
      description: "Türk mutfağının vazgeçilmezi",
      imageUrl: "https://cdn.yemek.com/mnresize/1250/833/uploads/2019/01/kuru-fasulye-tarifi.jpg",
      cookingTime: "60 dakika",
      difficulty: "Kolay"
    }
  ]);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Tarifler</h1>
      <div style={styles.searchBar}>
        <input
          type="text"
          placeholder="Tarif ara..."
          style={styles.searchInput}
        />
      </div>
      <div style={styles.grid}>
        {recipes.map(recipe => (
          <RecipeCard key={recipe.id} recipe={recipe} />
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '2rem'
  },
  title: {
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: '2rem'
  },
  searchBar: {
    marginBottom: '2rem',
    display: 'flex',
    justifyContent: 'center'
  },
  searchInput: {
    width: '100%',
    maxWidth: '500px',
    padding: '0.8rem',
    fontSize: '1rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    outline: 'none'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '2rem',
    padding: '1rem'
  }
};

export default RecipeList;
