import React from 'react';
import { useNavigate } from 'react-router-dom';

const RecipeCard = ({ recipe }) => {
  const navigate = useNavigate();

  return (
    <div style={styles.card} onClick={() => navigate(`/recipe/${recipe.id}`)}>
      <div style={styles.imageContainer}>
        <img src={recipe.imageUrl} alt={recipe.title} style={styles.image} />
        <div style={styles.category}>{recipe.category}</div>
      </div>
      <div style={styles.content}>
        <h3 style={styles.title}>{recipe.title}</h3>
        <p style={styles.description}>{recipe.description}</p>
        <div style={styles.details}>
          <span style={styles.badge}>{recipe.cookingTime}</span>
          <span style={styles.badge}>{recipe.difficulty}</span>
        </div>
      </div>
    </div>
  );
};

const styles = {
  card: {
    backgroundColor: 'white',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    overflow: 'hidden',
    cursor: 'pointer',
    transition: 'transform 0.2s, box-shadow 0.2s',
    height: '100%',
    display: 'flex',
    flexDirection: 'column'
  },
  imageContainer: {
    position: 'relative',
    width: '100%',
    height: '200px'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover'
  },
  category: {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: 'rgba(44, 62, 80, 0.9)',
    color: 'white',
    padding: '4px 8px',
    borderRadius: '4px',
    fontSize: '0.8rem'
  },
  content: {
    padding: '1rem',
    flex: 1,
    display: 'flex',
    flexDirection: 'column'
  },
  title: {
    margin: '0 0 0.5rem 0',
    color: '#2c3e50',
    fontSize: '1.25rem'
  },
  description: {
    color: '#666',
    fontSize: '0.9rem',
    margin: '0 0 1rem 0',
    flex: 1
  },
  details: {
    display: 'flex',
    gap: '0.5rem'
  },
  badge: {
    backgroundColor: '#e9ecef',
    padding: '4px 8px',
    borderRadius: '4px',
    fontSize: '0.8rem',
    color: '#495057'
  }
};

export default RecipeCard;
