import axios from 'axios';

const API_KEY = 'bZJJR7N-xwnVxUE--r3mQAz7kV5baGMGPcCD_7kdRw8';  // Buraya kendi API anahtarınızı eklemelisiniz

export const fetchRecipeImage = async (query) => {
  try {
    const response = await axios.get('https://api.unsplash.com/photos/random', {
      params: {
        query: query, // Arama yapmak için kullanacağımız tarifi başlığı
        client_id: API_KEY, // API anahtarınız
        orientation: 'landscape', // Görselin yatay olması için
      },
    });
    return response.data[0]?.urls?.regular || 'https://via.placeholder.com/200'; // Fallback görsel
  } catch (error) {
    console.error("Error fetching image:", error);
    return 'https://via.placeholder.com/200'; // Hata durumunda fallback görsel
  }
};
