import React, { useState, useEffect } from 'react';
import { FaPlus, FaSearch } from 'react-icons/fa';

// Buraya Unsplash API Anahtarınızı eklemeyi unutmayın
const API_KEY = 'bZJJR7N-xwnVxUE--r3mQAz7kV5baGMGPcCD_7kdRw8'; 

const RecipeSuggestion = () => {
  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [imageUrls, setImageUrls] = useState({});

  const sampleRecipes = [
    {
      id: 1,
      title: "Mercimek Çorbası",
      description: "Kırmızı mercimek, havuç ve patatesle hazırlanan besleyici çorba",
      category: "Çorbalar",
      ingredients: ["mercimek", "soğan", "havuç", "patates", "sıvı yağ", "tuz", "karabiber"]
    },
    {
      id: 2,
      title: "Karnıyarık",
      description: "Patlıcan severlerin vazgeçilmezi",
      category: "Ana Yemekler",
      ingredients: ["patlıcan", "kıyma", "soğan", "domates", "biber", "sıvı yağ", "tuz"]
    },
    {
      id: 3,
      title: "Menemen",
      description: "Klasik Türk kahvaltısı",
      category: "Kahvaltılık",
      ingredients: ["yumurta", "domates", "biber", "soğan", "sıvı yağ", "tuz"]
    }
  ];

  const commonIngredients = [
    'domates', 'soğan', 'patates', 'havuç', 'biber',
    'yumurta', 'un', 'süt', 'yoğurt', 'peynir',
    'tavuk', 'kıyma', 'pirinç', 'makarna', 'mercimek',
    'patlıcan', 'kabak', 'sarımsak', 'salatalık', 'maydanoz',
    'tereyağı', 'zeytinyağı', 'karabiber', 'tuz', 'pul biber'
  ];

  const addIngredient = () => {
    if (newIngredient && !ingredients.includes(newIngredient)) {
      setIngredients([...ingredients, newIngredient]);
      setNewIngredient('');
    }
  };

  const removeIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const findRecipes = async () => {
    setIsLoading(true);
    const suggestedRecipes = sampleRecipes.filter(recipe => {
      const requiredIngredients = recipe.ingredients.map(ing => ing.toLowerCase());
      const matchCount = ingredients.filter(ing => requiredIngredients.includes(ing.toLowerCase())).length;
      return matchCount >= Math.min(3, requiredIngredients.length / 2);
    });

    // Görselleri Unsplash API üzerinden çekme
    for (const recipe of suggestedRecipes) {
      const response = await fetch(`https://api.unsplash.com/photos/random?query=${recipe.title}&client_id=${API_KEY}`);
      const data = await response.json();
      if (data.length > 0) {
        setImageUrls((prev) => ({
          ...prev,
          [recipe.id]: data[0].urls.small // Görsel URL'si
        }));
      }
    }

    setSuggestions(suggestedRecipes);
    setIsLoading(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addIngredient();
    }
  };

  const getRecipeImage = (recipeId) => {
    return imageUrls[recipeId] || "https://via.placeholder.com/500x300.png?text=No+Image+Available"; // Placeholder görsel
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Malzemelerinize Göre Tarif Bulun</h2>
      
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={newIngredient}
          onChange={(e) => setNewIngredient(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Malzeme ekleyin..."
          style={styles.input}
        />
        <button onClick={addIngredient} style={styles.addButton}>
          <FaPlus /> Ekle
        </button>
      </div>

      <div style={styles.commonIngredients}>
        <h3 style={styles.subtitle}>Sık Kullanılan Malzemeler:</h3>
        <div style={styles.commonIngredientsList}>
          {commonIngredients.map((ingredient, index) => (
            <button
              key={index}
              onClick={() => setNewIngredient(ingredient)}
              style={styles.commonIngredientButton}
            >
              {ingredient}
            </button>
          ))}
        </div>
      </div>

      <div style={styles.ingredientsList}>
        {ingredients.map((ingredient, index) => (
          <span key={index} style={styles.ingredient}>
            {ingredient}
            <button 
              onClick={() => removeIngredient(index)}
              style={styles.removeButton}
            >
              ×
            </button>
          </span>
        ))}
      </div>

      <button 
        onClick={findRecipes} 
        style={styles.searchButton}
        disabled={ingredients.length === 0 || isLoading}
      >
        {isLoading ? 'Aranıyor...' : <><FaSearch /> Tarif Bul</>}
      </button>

      {suggestions.length > 0 && (
        <div style={styles.suggestions}>
          <h3 style={styles.subtitle}>Önerilen Tarifler:</h3>
          <div style={styles.suggestionsGrid}>
            {suggestions.map((recipe) => (
              <div key={recipe.id} style={styles.recipeCard}>
                <img 
                  src={getRecipeImage(recipe.id)} 
                  alt={recipe.title}
                  style={styles.recipeImage}
                />
                <div style={styles.recipeContent}>
                  <h4 style={styles.recipeTitle}>{recipe.title}</h4>
                  <p style={styles.recipeDescription}>{recipe.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    fontSize: '2rem',
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: '2rem',
  },
  subtitle: {
    fontSize: '1.5rem',
    color: '#2c3e50',
    marginBottom: '1rem',
  },
  inputContainer: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '2rem',
  },
  input: {
    flex: 1,
    padding: '0.8rem',
    fontSize: '1rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    outline: 'none',
  },
  addButton: {
    padding: '0.8rem 1.5rem',
    backgroundColor: '#2ecc71',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    fontSize: '1rem',
  },
  commonIngredients: {
    marginBottom: '2rem',
  },
  commonIngredientsList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.5rem',
  },
  commonIngredientButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#f0f0f0',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    color: '#666',
  },
  ingredientsList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.5rem',
    marginBottom: '2rem',
  },
  ingredient: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    padding: '0.5rem 1rem',
    backgroundColor: '#3498db',
    color: 'white',
    borderRadius: '20px',
    fontSize: '0.9rem',
  },
  removeButton: {
    background: 'none',
    border: 'none',
    color: 'white',
    cursor: 'pointer',
    fontSize: '1.2rem',
    padding: '0 0.3rem',
  },
  searchButton: {
    padding: '1rem 2rem',
    backgroundColor: '#e74c3c',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    fontSize: '1.1rem',
    margin: '0 auto',
  },
  suggestions: {
    marginTop: '3rem',
  },
  suggestionsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: '1.5rem',
  },
  recipeCard: {
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  },
  recipeImage: {
    width: '100%',
    height: 'auto',
  },
  recipeContent: {
    padding: '1rem',
  },
  recipeTitle: {
    fontSize: '1.2rem',
    color: '#2c3e50',
    marginBottom: '1rem',
  },
  recipeDescription: {
    fontSize: '1rem',
    color: '#7f8c8d',
    marginBottom: '1rem',
  },
};

export default RecipeSuggestion;
