import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddRecipe = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState({
    title: '',
    description: '',
    ingredients: [''],
    instructions: [''],
    cookingTime: '',
    difficulty: 'Kolay',
    imageUrl: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...recipe.ingredients];
    newIngredients[index] = value;
    setRecipe(prev => ({
      ...prev,
      ingredients: newIngredients
    }));
  };

  const addIngredient = () => {
    setRecipe(prev => ({
      ...prev,
      ingredients: [...prev.ingredients, '']
    }));
  };

  const handleInstructionChange = (index, value) => {
    const newInstructions = [...recipe.instructions];
    newInstructions[index] = value;
    setRecipe(prev => ({
      ...prev,
      instructions: newInstructions
    }));
  };

  const addInstruction = () => {
    setRecipe(prev => ({
      ...prev,
      instructions: [...prev.instructions, '']
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // API'ye gönderme işlemi burada yapılacak
    console.log('Yeni tarif:', recipe);
    navigate('/recipes');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Yeni Tarif Ekle</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Tarif Adı:</label>
          <input
            type="text"
            name="title"
            value={recipe.title}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Açıklama:</label>
          <textarea
            name="description"
            value={recipe.description}
            onChange={handleChange}
            style={styles.textarea}
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Malzemeler:</label>
          {recipe.ingredients.map((ingredient, index) => (
            <div key={index} style={styles.listItem}>
              <input
                type="text"
                value={ingredient}
                onChange={(e) => handleIngredientChange(index, e.target.value)}
                style={styles.input}
                placeholder={`${index + 1}. malzeme`}
              />
            </div>
          ))}
          <button type="button" onClick={addIngredient} style={styles.addButton}>
            + Malzeme Ekle
          </button>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Hazırlanışı:</label>
          {recipe.instructions.map((instruction, index) => (
            <div key={index} style={styles.listItem}>
              <textarea
                value={instruction}
                onChange={(e) => handleInstructionChange(index, e.target.value)}
                style={styles.textarea}
                placeholder={`${index + 1}. adım`}
              />
            </div>
          ))}
          <button type="button" onClick={addInstruction} style={styles.addButton}>
            + Adım Ekle
          </button>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Pişirme Süresi:</label>
          <input
            type="text"
            name="cookingTime"
            value={recipe.cookingTime}
            onChange={handleChange}
            style={styles.input}
            placeholder="örn: 30 dakika"
            required
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Zorluk Seviyesi:</label>
          <select
            name="difficulty"
            value={recipe.difficulty}
            onChange={handleChange}
            style={styles.select}
          >
            <option value="Kolay">Kolay</option>
            <option value="Orta">Orta</option>
            <option value="Zor">Zor</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Resim URL:</label>
          <input
            type="url"
            name="imageUrl"
            value={recipe.imageUrl}
            onChange={handleChange}
            style={styles.input}
            placeholder="https://..."
          />
        </div>

        <button type="submit" style={styles.submitButton}>
          Tarifi Kaydet
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '2rem'
  },
  title: {
    color: '#2c3e50',
    textAlign: 'center',
    marginBottom: '2rem'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem'
  },
  formGroup: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem'
  },
  label: {
    color: '#2c3e50',
    fontSize: '0.9rem',
    fontWeight: 'bold'
  },
  input: {
    padding: '0.8rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '1rem'
  },
  textarea: {
    padding: '0.8rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '1rem',
    minHeight: '100px',
    resize: 'vertical'
  },
  select: {
    padding: '0.8rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '1rem'
  },
  listItem: {
    marginBottom: '0.5rem'
  },
  addButton: {
    padding: '0.5rem',
    backgroundColor: '#e9ecef',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    color: '#495057',
    marginTop: '0.5rem'
  },
  submitButton: {
    padding: '1rem',
    backgroundColor: '#2c3e50',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    fontSize: '1rem',
    cursor: 'pointer',
    marginTop: '1rem'
  }
};

export default AddRecipe;
