// src/utils/imageUtils.js

const getRecipeImage = async () => {
    try {
      const response = await fetch('https://foodish-api.herokuapp.com/api/');
      const data = await response.json();
      return data.image;
    } catch (error) {
      console.error('Resim yükleme hatası:', error);
      // Hata durumunda varsayılan bir resim döndür
      return 'https://raw.githubusercontent.com/surhud004/Foodish/main/images/burger/burger10.jpg';
    }
  };
  
  export default getRecipeImage;
