// src/utils/fetchRecipeImage.js

export const fetchRecipeImage = async () => {
    const accessKey = 'bZJJR7N-xwnVxUE--r3mQAz7kV5baGMGPcCD_7kdRw8'; // Buraya Unsplash API erişim anahtarınızı girin
    const url = `https://api.unsplash.com/photos/random?query=recipe&client_id=${accessKey}`;
  
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data[0]?.urls?.small || 'https://example.com/placeholder.jpg'; // Eğer resim bulunamazsa placeholder kullanılır
    } catch (error) {
      console.error("Resim yükleme hatası:", error);
      return 'https://example.com/placeholder.jpg'; // Hata durumunda fallback image kullanılır
    }
  };
